"""Created on Sun Sep 24 15:02:46 2023@author: wangbinb"""import numpy as npfrom scipy.optimize import fsolvefrom scipy import interpolateimport ref_waterimport sediment_typeimport scipy.io as sioimport matplotlib.pyplot as pltplt.style.use(['science', 'no-latex'])water = ref_water.artificial_water(temperature = 20, density = 1000.)dia = np.linspace(1e-6, 1.1e-2, 1000)v = np.zeros(len(dia))i = 0for dia_i in dia:    sand = sediment_type.sand(water, dia_i)    v[i] = -sand.ws    i = i+1        data_measured = sio.loadmat('sediment_settling_literature/literature_sediment_settling.mat')d1 = data_measured['Cheng1997']['d1'][0][0][0]V1 = data_measured['Cheng1997']['v1_20degreeC'][0][0][0]d2 = data_measured['Cheng1997']['d2'][0][0][0]V2 = data_measured['Cheng1997']['v2_15degreeC'][0][0][0]d3 = data_measured['EH1967']['dv'][0][0][0]V3 = data_measured['EH1967']['ws_10degreeC'][0][0][0]d4 = np.array([0.463, 0.39, 0.328, 0.275, 0.231, 0.181])*1e-3V4 = np.array([6, 4.91, 4.05, 3.22, 2.7, 2.01])*1e-2plt.plot(d1,V1,'X',label='Cheng1997, dataset 1')plt.plot(d2,V2,'<',label='Cheng1997, dataset 2')plt.plot(d3,V3,'s',label='EH1967')plt.plot(d4,V4,'o',label='CE2007')plt.plot(dia,v, label='model, this study')plt.xlabel('$d$ (m)')plt.ylabel('$V_s$ (m/s)')plt.xscale('log')plt.legend()plt.savefig('vel_dia.pdf')